"""
   I saved SVG weblogo from Jaspar and use
   this script to to normalize characters
"""

def normalize(char):
    tokens = char.split()
    count = 0
    x = list()
    y = list()
    for t in tokens:
        if t == "M" or t == "L" or t == "Z":
            continue
        if count % 2 == 0:
            x.append(float(t))
        else:
            y.append(float(t))
        count += 1
    minx = min(x)
    miny = min(y)
    maxy = max(y) - miny
    if len(x) != len(y):
        print("Something wrong")
        return
    for i in range(len(x)):
        x[i] = x[i] - minx
        y[i] = (y[i] - miny) / maxy * 100.0
        print(x[i], y[i], end=",", sep=",")
    print()

